var SaChart = echarts.init(document.getElementById('Sales')); 
    SaChart.setOption({
        grid:{'x':20,y:25,x2:10,y2:60},
        calculable : true,
        xAxis : [
            {
                type : 'category',
                data : ["53度\n川小二","金六福\n三星酒38","42度\n4L川小二","金六福\n二星酒52","38度\n绵竹大曲"],
                axisLabel:{  
                     interval:0,
                     textStyle:{
                        fontFamily:"微软雅黑"
                     }
                }, 
                axisLine: {
                    lineStyle: {
                        color: '#ccc'
                    },
                },
                axisTick:{
                    show:false
                }
            }
        ],
        yAxis : [
            {
                type : 'value',
                axisLine: {
                    lineStyle: {
                        color: '#ccc'
                    }
                }
            }
        ],
        series : [
            {
                itemStyle: {
                    normal: {
                        label: {
                            show: true,
                            position: 'top',
